#include <iostream>
#include <map>
#include <set>
#include <vector>
using namespace std;
#define mp make_pair
#define forf(i, n) for (int i = 0; i < n; i++)
typedef vector<int> vi;

class TwoDiagonals {
 public:
  int maxPoints(vi x, vi y) {
    int n = x.size();
    set<pair<int, int>> pts;
    map<int, set<int>> nw_lines, ne_lines;
    forf(i, n) {
      pts.insert(mp(x[i], y[i]));
      nw_lines[x[i] + y[i]].insert(i);
      ne_lines[x[i] - y[i]].insert(i);
    }

    int max_intersection_size = 0;
    // consider all pairs of lines
    for (const auto& kv : nw_lines) {
      for (const auto& kv2 : ne_lines) {
        int intersection_size = kv.second.size() + kv2.second.size();
        int y_intercept_nw = kv.first;
        int y_intercept_ne = -kv2.first;
        if ((y_intercept_nw + y_intercept_ne) % 2 == 0) {
          int x_line_intersection = (y_intercept_nw - y_intercept_ne) / 2;
          int y_line_intersection = y_intercept_nw - x_line_intersection;
          if (pts.count(mp(x_line_intersection, y_line_intersection))) {
            intersection_size--;
          }
        }
        max_intersection_size = max(max_intersection_size, intersection_size);
      }
    }
    return max_intersection_size;
  }
};

